import React, { useState, useContext } from "react";

import axios from "axios";

import GeneralContext from "./GeneralContext";

import { Tooltip, Grow } from "@mui/material";

import {
  BarChartOutlined,
  KeyboardArrowDown,
  KeyboardArrowUp,
  MoreHoriz,
} from "@mui/icons-material";

import { watchlist } from "../data/data";
import { DoughnutChart } from "./DoughnoutChart";
import "./WatchList.css";

const labels = watchlist.map((subArray) => subArray["name"]);

const WatchList = () => {
  const data = {
    labels,
    datasets: [
      {
        label: "Price",
        data: watchlist.map((stock) => stock.price),
        backgroundColor: [
          "rgba(255, 99, 132, 0.5)",
          "rgba(54, 162, 235, 0.5)",
          "rgba(255, 206, 86, 0.5)",
          "rgba(75, 192, 192, 0.5)",
          "rgba(153, 102, 255, 0.5)",
          "rgba(255, 159, 64, 0.5)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="watchlist">
      <table>
        <thead>
          <tr>
            <th>Stock</th>
            <th>Price</th>
            <th>Change</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {watchlist.map((stock, index) => {
            return (
              <tr key={index}>
                <td>{stock.name}</td>
                <td>â‚¹{stock.price}</td>
                <td>
                  {stock.isDown ? (
                    <span className="down">
                      <KeyboardArrowDown /> {stock.percent}
                    </span>
                  ) : (
                    <span className="up">
                      <KeyboardArrowUp /> {stock.percent}
                    </span>
                  )}
                </td>
                <td>
                  <Tooltip title="Chart">
                    <BarChartOutlined className="action-icon" />
                  </Tooltip>
                  <Tooltip title="More">
                    <MoreHoriz className="action-icon" />
                  </Tooltip>
                  <button>Buy</button>
                  <button>Sell</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <DoughnutChart data={data} />
    </div>
  );
};

export default WatchList;
