import React from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, PointElement, LineElement } from "chart.js";
import { Doughnut, Line } from "react-chartjs-2";

ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement
);

const DoughnutChart = ({ data }) => {
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 12,
            family: "'Segoe UI', 'Roboto', sans-serif",
            weight: '300'
          },
          color: 'rgb(73, 73, 73)',
          padding: 15
        }
      },
      tooltip: {
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        titleColor: 'rgb(73, 73, 73)',
        titleFont: {
          size: 12,
          weight: '400',
          family: "'Segoe UI', 'Roboto', sans-serif",
        },
        bodyColor: 'rgb(73, 73, 73)',
        bodyFont: {
          size: 12,
          family: "'Segoe UI', 'Roboto', sans-serif",
        },
        borderColor: 'rgb(236, 235, 235)',
        borderWidth: 1,
        padding: 10,
        boxPadding: 5
      }
    }
  };

  return (
    <div className="container" style={{ 
      backgroundColor: '#fff',
      borderRadius: '4px',
      boxShadow: '0px 0px 4px 1px rgb(236, 235, 235)',
      padding: '20px',
      width: '100%'
    }}>
      <div className="charts-grid" style={{ 
        display: 'flex',
        gap: '40px'
      }}>
        <div style={{ 
          flex: '0 0 450px',
          height: '400px',
          position: 'relative'
        }}>
          <Doughnut 
            data={data} 
            options={{
              ...options,
              cutout: '70%'
            }}
          />
        </div>
        <div style={{ 
          flex: '1',
          height: '400px',
          minWidth: '0'
        }}>
          <Line 
            data={data} 
            options={{
              ...options,
              scales: {
                y: {
                  beginAtZero: true,
                  grid: {
                    color: 'rgb(243, 243, 243)',
                  },
                  ticks: {
                    color: 'rgb(145, 145, 145)',
                    font: {
                      size: 11,
                      family: "'Segoe UI', 'Roboto', sans-serif",
                      weight: '300'
                    }
                  }
                },
                x: {
                  grid: {
                    color: 'rgb(243, 243, 243)',
                  },
                  ticks: {
                    color: 'rgb(145, 145, 145)',
                    font: {
                      size: 11,
                      family: "'Segoe UI', 'Roboto', sans-serif",
                      weight: '300'
                    }
                  }
                }
              }
            }}
          />
        </div>
      </div>
    </div>
  );
};

export { DoughnutChart };
