import React from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, PointElement, LineElement } from "chart.js";
import { Doughnut, Line } from "react-chartjs-2";

ChartJS.register(
  ArcElement, 
  Tooltip, 
  Legend,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement
);

export function DoughnutChart({ data }) {
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    cutout: '75%',
    plugins: {
      legend: {
        position: 'top',
        align: 'start',
        labels: {
          boxWidth: 12,
          padding: 15,
          font: {
            size: 11,
            family: "'Segoe UI', 'Roboto', sans-serif",
            weight: '400'
          },
          color: 'rgb(73, 73, 73)'
        }
      },
      tooltip: {
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        titleColor: 'rgb(73, 73, 73)',
        bodyColor: 'rgb(73, 73, 73)',
        borderColor: 'rgb(236, 235, 235)',
        borderWidth: 1,
        padding: 8,
        bodyFont: {
          size: 11,
          family: "'Segoe UI', 'Roboto', sans-serif"
        }
      }
    }
  };

  return (
    <div style={{ 
      width: '100%', 
      backgroundColor: '#fff', 
      padding: '20px', 
      borderRadius: '4px', 
      boxShadow: '0px 0px 4px 1px rgb(236, 235, 235)',
      marginBottom: '20px'
    }}>
      <div style={{ 
        display: 'flex', 
        gap: '40px',
        alignItems: 'flex-start',
        justifyContent: 'flex-start'
      }}>
        <div style={{ 
          width: '380px', 
          height: '380px',
          padding: '10px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}>
          <Doughnut data={data} options={options} />
        </div>
        <div style={{ 
          width: '380px',
          height: '380px',
          padding: '10px'
        }}>
          <Line 
            data={data} 
            options={{
              ...options,
              plugins: {
                ...options.plugins,
                legend: {
                  ...options.plugins.legend,
                  position: 'top'
                }
              },
              scales: {
                y: {
                  beginAtZero: true,
                  grid: {
                    color: 'rgb(243, 243, 243)',
                    drawBorder: false
                  },
                  ticks: {
                    color: 'rgb(145, 145, 145)',
                    font: {
                      size: 11,
                      family: "'Segoe UI', 'Roboto', sans-serif",
                      weight: '300'
                    },
                    padding: 8
                  }
                },
                x: {
                  grid: {
                    color: 'rgb(243, 243, 243)',
                    drawBorder: false
                  },
                  ticks: {
                    color: 'rgb(145, 145, 145)',
                    font: {
                      size: 11,
                      family: "'Segoe UI', 'Roboto', sans-serif",
                      weight: '300'
                    },
                    padding: 8
                  }
                }
              }
            }} 
          />
        </div>
      </div>
    </div>
  );
}
