import React from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, PointElement, LineElement } from "chart.js";
import { Doughnut, Line } from "react-chartjs-2";

ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement
);

const DoughnutChart = ({ data }) => {
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
    },
  };

  return (
    <div className="container">
      <div className="charts-grid" style={{ display: 'flex', justifyContent: 'space-between', padding: '20px' }}>
        <div className="chart-container" style={{ width: '45%', height: '400px' }}>
          <Doughnut data={data} />
        </div>
        <div className="chart-container" style={{ width: '45%', height: '400px' }}>
          <Line data={data} options={options} />
        </div>
      </div>
    </div>
  );
};

export { DoughnutChart };
