import React from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, PointElement, LineElement } from "chart.js";
import { Doughnut, Line } from "react-chartjs-2";

ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement
);

const DoughnutChart = ({ data }) => {
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 12,
            family: "'Segoe UI', 'Roboto', sans-serif",
            weight: '300'
          },
          color: 'rgb(73, 73, 73)',
          padding: 15
        }
      },
      tooltip: {
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        titleColor: 'rgb(73, 73, 73)',
        titleFont: {
          size: 12,
          weight: '400',
          family: "'Segoe UI', 'Roboto', sans-serif",
        },
        bodyColor: 'rgb(73, 73, 73)',
        bodyFont: {
          size: 12,
          family: "'Segoe UI', 'Roboto', sans-serif",
        },
        borderColor: 'rgb(236, 235, 235)',
        borderWidth: 1,
        padding: 10,
        boxPadding: 5
      }
    }
  };

  return (
    <div className="container" style={{ 
      backgroundColor: '#fff',
      borderRadius: '4px',
      boxShadow: '0px 0px 4px 1px rgb(236, 235, 235)',
      padding: '20px',
      position: 'relative',
      zIndex: '1',
      width: '100%'
    }}>
      <div className="charts-grid" style={{ 
        display: 'flex',
        gap: '40px',
        alignItems: 'center',
        width: '100%'
      }}>
        <div className="chart-container" style={{ 
          width: '35%',
          height: '400px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'flex-start',
          minWidth: '400px'
        }}>
          <Doughnut data={data} options={{
            ...options,
            maintainAspectRatio: false,
            layout: {
              padding: {
                left: 0,
                right: 0,
                top: 20,
                bottom: 20
              }
            }
          }} />
        </div>
        <div className="chart-container" style={{ 
          width: '65%',
          height: '400px',
          flexGrow: 1
        }}>
          <Line data={data} options={{
            ...options,
            maintainAspectRatio: false,
            scales: {
              y: {
                beginAtZero: true,
                grid: {
                  color: 'rgb(243, 243, 243)',
                },
                ticks: {
                  color: 'rgb(145, 145, 145)',
                  font: {
                    size: 11,
                    family: "'Segoe UI', 'Roboto', sans-serif",
                    weight: '300'
                  }
                }
              },
              x: {
                grid: {
                  color: 'rgb(243, 243, 243)',
                },
                ticks: {
                  color: 'rgb(145, 145, 145)',
                  font: {
                    size: 11,
                    family: "'Segoe UI', 'Roboto', sans-serif",
                    weight: '300'
                  }
                }
              }
            }
          }} />
        </div>
      </div>
    </div>
  );
};

export { DoughnutChart };
