import React from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, PointElement, LineElement } from "chart.js";
import { Doughnut, Line } from "react-chartjs-2";

ChartJS.register(
  ArcElement, 
  Tooltip, 
  Legend,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement
);

export function DoughnutChart({ data }) {
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    cutout: '70%',
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 12,
            weight: '300'
          },
          padding: 15
        }
      }
    }
  };

  return (
    <div style={{ width: '100%', backgroundColor: '#fff', padding: '20px', borderRadius: '4px', boxShadow: '0px 0px 4px 1px rgb(236, 235, 235)' }}>
      <div style={{ display: 'flex', gap: '40px' }}>
        <div style={{ width: '400px', height: '350px' }}>
          <Doughnut data={data} options={options} />
        </div>
        <div style={{ flex: 1, height: '350px' }}>
          <Line 
            data={data} 
            options={{
              ...options,
              plugins: {
                ...options.plugins,
                legend: {
                  ...options.plugins.legend,
                  position: 'top'
                }
              },
              scales: {
                y: {
                  beginAtZero: true,
                  grid: {
                    color: '#f3f3f3'
                  }
                },
                x: {
                  grid: {
                    color: '#f3f3f3'
                  }
                }
              }
            }} 
          />
        </div>
      </div>
    </div>
  );
}
